class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        Mod=10**9 +7
        n = len(nums)
        dp = [[[-1] * 51 for _ in range(51)] for _ in range(n)]  
        def solve(idx: int, prev1: int, prev2: int) -> int:
            if idx >= n:
                return 1
            if dp[idx][prev1][prev2] != -1:
                return dp[idx][prev1][prev2]
            ans = 0
            for i in range(nums[idx] + 1):
                arr1_val = i
                arr2_val = nums[idx] - i
                if arr1_val >= prev1 and arr2_val <= prev2:
                    ans = (ans + solve(idx + 1, arr1_val, arr2_val)) % Mod
            dp[idx][prev1][prev2] = ans
            return ans
        return solve(0, 0, 50)
