class Solution {
public:
    int mod=1e9+7;
    int dp[2001][51][51];
    int solve(int ind,vector<int>& nums,int inc,int dec){
        if(ind==nums.size())
            return 1;

        if(dp[ind][inc][dec]!=-1)
            return dp[ind][inc][dec];

        int ans=0;
        for(int i=0;i<=nums[ind];i++){
            if(i>=inc && (nums[ind]-i)<=dec){
                ans=(ans%mod+solve(ind+1,nums,i,nums[ind]-i)%mod)%mod;
            }
        }
        return dp[ind][inc][dec]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(0,nums,0,50)%mod;
    }
};
