class Solution {
public:
    static const int MOD = 1000000007;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(
            n, vector<vector<int>>(51, vector<int>(51, -1)));
        return findPairs(nums, 0, 0, 50);
    }

private:
    vector<vector<vector<int>>> dp;

    int findPairs(vector<int>& nums, int index, int p1, int p2) {
        if (index >= nums.size()) {
            return 1;
        }

        if (dp[index][p1][p2] != -1) {
            return dp[index][p1][p2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[index]; ++i) {
            int a = i;
            int b = nums[index] - i;
            if (a >= p1 && b <= p2) {
                ans = (ans + findPairs(nums, index + 1, a, b)) % MOD;
            }
        }

        dp[index][p1][p2] = ans;
        return ans;
    }
};
