class Solution {
private:
    static const int MOD = 1'000'000'007;

public:
    int countOfPairs(std::vector<int>& values) {
        int n  = values.size();

        long long m[n][51][51];

        memset(m, -1, sizeof(m));


        return (int) solve(values, 0, 0, values[0], m);
    }

private:
    long long solve(std::vector<int>& values, int k, int v1, int v2, long long m[][51][51]) {
        if (k >= values.size()) return 1; 
        if (m[k][v1][v2] != -1) return m[k][v1][v2];  

        long long ans = 0;
        for (int i = v1; i <= values[k]; i++) {
            for (int j = v2; j >= 0; j--) {
                if (i + j == values[k]) {
                    ans = (ans + solve(values, k + 1, i, j, m)) % MOD;
                }
            }
        }
        m[k][v1][v2] = ans;  
        return ans;
    }
};
