class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] dp = new long[n][51];
        int mod = (int)1e9 + 7;
        for (int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }
        for (int ii = 1; ii < n; ii++) {
            int sum = nums[ii];
            for (int i = 0; i <= sum; i++) {
                int rest = sum - i;
                for (int j = i; j >= 0; j--) {
                    int dif = nums[ii-1] - j;
                    if (dif >= rest) {
                        dp[ii][i] += dp[ii-1][j];
                        dp[ii][i] %= mod;
                    }
                }
            }
        }
        long res = 0;
        for (int i = 0; i <= nums[n-1]; i++) {
            res += dp[n-1][i];
            res %= mod;
        }
        return (int)res;
    }
}
