const int MOD = 1000000007;
const int MAX_VAL = 60;

class Solution {
public:
    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<long long>>> dppp(size, vector<vector<long long>>(MAX_VAL + 1, vector<long long>(MAX_VAL + 1, -1)));
        return solve(values, 0, 0, values[0], dppp);
    }


    long long solve(vector<int>& values, int ind, int v1, int v2, vector<vector<vector<long long>>>& dppp) {
        if (ind >= values.size()) return 1;
        if (dppp[ind][v1][v2] != -1) return dppp[ind][v1][v2];

        long long res = 0;
        for (int i = v1; i <= values[ind]; i++) {
            for (int j = v2; j >= 0; j--) {
                if (i + j == values[ind]) {
                    res = (res + solve(values, ind + 1, i, j, dppp)) % MOD;
                }
            }
        }
        dppp[ind][v1][v2] = res;
        return res;
    }
};
