class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        p=max(nums)+1
        dp=[]
        for i in range(n):
            dp.append([-1]*p)

        def fun(i,prev):
            if i==n:
                return 1
            if dp[i][prev]!=-1:
                return dp[i][prev]

            ans=0
            for j in range(prev,nums[i]+1):
                if i==0 or nums[i]-j<=nums[i-1]-prev:
                    ans=(ans+fun(i+1,j))%1000000007
            dp[i][prev]=ans
            return ans

        return fun(0,0)
