typedef long long ll;
const int MOD=1e9+7;
class Solution {
public:
    ll f(int ind, int x, int y, vector<int> &nums, int n, vector<vector<vector<ll>>> &dp)
    {
        if(ind==n)
            return 1;
        if(dp[ind][x][y]!=-1)
            return dp[ind][x][y];
        ll ans=0;
        for(int i=x;i<=nums[ind];i++)
        {
            for(int j=y;j>=0;j--)
            {
                if(i+j==nums[ind])
                    ans=(ans+f(ind+1, i, j, nums, n, dp))%MOD;
            }
        }
        return dp[ind][x][y]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return f(0, 0, nums[0], nums, n, dp);
    }
};
