class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        using ll = long long;
        ll MOD = 1e9 + 7;

        int n = nums.size();
        int m = *max_element(nums.begin(), nums.end());

        ll dp[n][m + 1]; memset(dp, 0, sizeof(dp));
        ll pre_sum[n][m + 2]; memset(pre_sum, 0, sizeof(pre_sum));
        for (int j = 0; j <= nums[0]; ++ j) {
            dp[0][j] = 1;
            pre_sum[0][j] = j;
        }
        pre_sum[0][nums[0] + 1] = nums[0] + 1;
        for (int i = 1; i < n; ++ i) {
            for (int j = 0; j <= nums[i]; ++ j) {
                int j2 = min(j, nums[i - 1] - nums[i] + j);
                if (j2 < 0) continue;
                dp[i][j] = pre_sum[i - 1][j2 + 1];
                pre_sum[i][j] = ( j == 0? 0: pre_sum[i][j - 1] + dp[i][j - 1] ) % MOD;
            }
            pre_sum[i][nums[i] + 1] = ( pre_sum[i][nums[i]] + dp[i][nums[i]] ) % MOD;
        }

        ll res = 0;
        for (int j = 0; j <= m; ++ j) res = (res + dp[n - 1][j]) % MOD;
        return res;
    }
};
