class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        ios_base::sync_with_stdio(false);
        cin.tie(NULL);

        int M = 0;
        for (int i : nums) M = max(i, M);

        long long mod = pow(10,9) + 7;
        long long n = nums.size();
        vector<vector<vector<long long>>> dp(n+1, vector<vector<long long>>(M+1, vector<long long>(M+1)));

        for (long long x = 0; x <= nums[0]; x++) {
            dp[0][x][nums[0]-x] = 1;
        }

        for (long long i = 1; i < n; i++) {
            long long subject = nums[i];

            for (long long t1 = 0; t1 <= nums[i-1]; t1++) {
                long long t2 = nums[i-1] - t1;
                for (long long x = t1; x <= subject; x++) {
                    if (subject-x <= t2) {
                        dp[i][x][subject-x] += dp[i-1][t1][t2];
                        dp[i][x][subject-x] %= mod;
                    }
                }
            }
        }

        long long ans = 0;
        for (long long x = 0; x <= nums[n-1]; x++) {
            ans += dp[n-1][x][nums[n-1]-x];
            ans %= mod;
        }
        return ans;

    }
};
