class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();

        vector<vector<long>> dp(n, vector<long>(51)); // dp[i][j] represents count of pairs ending with nums[i] where arr1[i] == j

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int a = 0; a <= nums[i - 1]; ++a) {
                if (a > nums[i]) break;
                for (int b = a; b <= nums[i]; ++b) {
                    if (nums[i - 1] - a >= nums[i] - b)
                        dp[i][b] = (dp[i][b] + dp[i - 1][a]) % MOD;
                }
            }
        }

        // Calculate the count of monotonic pairs
        int count = 0;
        for (int j = 0; j <= 50; ++j) {
            count = (count + dp[n - 1][j]) % MOD;
        }

        return count;
    }
};
