#define MOD 1000000007
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(51,0));

        for(int j=0; j<=nums[0]; j++){
            dp[0][j]=1;
        }

        for(int i=1; i<nums.size(); i++){
            for(int j=0; j<=nums[i]; j++){
                for(int x=0; x<=nums[i-1]; x++){
                    if(x<=j && nums[i-1]-x>=nums[i]-j){
                        dp[i][j]=((dp[i][j]%MOD)+(dp[i-1][x]%MOD))%MOD;
                    }
                }
            }
        }

        int ans=0;

        for(int i=0; i<dp[nums.size()-1].size(); i++){
            ans=((ans%MOD)+(dp[nums.size()-1][i]%MOD))%MOD;
        }

        return ans;
    }
};
