mod=10**9+7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        dp=[[0]*51 for _ in range(n)]
        for i in range(nums[0]+1):
            dp[0][i]=1
        for i in range(1,n):
            for j in range(nums[i]+1):
                for k in range(j+1):
                    if 0<=nums[i-1]-k<nums[i]-j:
                        continue
                    dp[i][j]=(dp[i-1][k]+dp[i][j])%mod
        return sum(dp[-1])%mod
