class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = 1000000007

        @cache
        def solve(i, prev1, prev2):
            if i == n:
                return 1

            ans = 0
            for val in range(nums[i] + 1):
                if val >= prev1 and (nums[i] - val) <= prev2 >= 0:
                    ans = (ans + solve(i + 1, val, nums[i] - val)) % mod

            return ans % mod


        return solve(0, 0, nums[0])
