class Solution {
    const int mod = 1e9+7;
    int solve(int idx,int prev1,int prev2,vector<int>& nums,int n,vector<vector<vector<int>>>& dp){
        if(idx==n) return 1;
        if(dp[idx][prev1][prev2]!=-1) return dp[idx][prev1][prev2];
        int res = 0;
        for(int i=0;i<=nums[idx];i++){
            int n1 = i;
            int n2 = nums[idx]-i;

            if(n1>=prev1 && n2<=prev2){
                res += solve(idx+1,n1,n2,nums,n,dp);
                res = res%mod;
            }
        }
        return dp[idx][prev1][prev2]=res;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(0,0,50,nums,n,dp);
    }
};
