int N = 1e9 + 7;

class Solution
{
public:
    int help(int z, int y, int x, vector<int> &nums, vector<vector<vector<int>>> &vec)
    {
        if (x >= nums.size())
        {
            return 1;
        }
        int result = 0;
        if (vec[x][y][z] != -1)
        {
            return vec[x][y][z];
        }
        for (int i = y; i <= nums[x]; ++i)
        {
            for (int j = z; j >= 0; --j)
            {
                if (nums[x]-j==i)
                {
                    result =result +  help(j, i, x+1, nums, vec);
                    result = result % N;
                }
            }
        }
        return vec[x][y][z] = result;
    }
    int countOfPairs(vector<int> &nums)
    {
        int n = nums.size();
        vector<vector<vector<int>>> vec(n, vector<vector<int>>(52, vector<int>(52, -1)));
        int a =0,b = 0;
        int an =  help(nums[0],a, b, nums, vec);
        return an;
    }
};
