class Solution {
    int maxi;
    int n;
    int mod=1e9+7;
    int solve(int i,int prev,int prev2,vector<int>&nums,vector<vector<vector<int>>>&dp){
        if(i==n) return 1;
        if(dp[i][prev][prev2]!=-1) return dp[i][prev][prev2];
        int ans=0;
        for(int x=prev;x<=nums[i];x++){
            int y=nums[i]-x;
            if(y<=prev2){
            ans=(ans+solve(i+1,x,y,nums,dp))%mod;
            }

        }
        return dp[i][prev][prev2]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        n=nums.size();

        maxi=*max_element(nums.begin(),nums.end());
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(maxi+1,vector<int>(maxi+1,-1)));
        return solve(0,0,maxi,nums,dp);
    }
};
