class Solution {
public:
    int mod=1e9+7;
    int f(int i,long long int last,long long int rev,int n,vector<int>& nums,vector<vector<vector<int>>> &dp){
        if(i==n){
            return 1;
        }
        if(dp[i][last][rev]!=-1) return dp[i][last][rev];

        long long int ans=0;
        for(int j=last;j<=nums[i];j++){
            if(rev>=nums[i]-j){
                ans=(ans+f(i+1,j,nums[i]-j,n,nums,dp))%mod;
            }    
        }
        return dp[i][last][rev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(52, -1)));
        return f(0,0,51,n,nums,dp);
    }
};
