class Solution {
public:
    int add(long long int a, long long int b) {
        int mod = (1e9)+7;
        return ((a%mod)+(b%mod))%mod;
    }
    int countOfPairs(const int pos, const int n, const int pu, const int pl, const vector<int>& nums, vector<vector<int> >&dp) {
        if(pos==n) return 1;
        int ans = 0, mod = (1e9)+7;
        for(int u=0;u<=nums[pos];u++) {
            int l = nums[pos]-u;
            if(pu<=u && pl>=l){
                if(dp[pos+1][u]==-1){
                    dp[pos+1][u] = countOfPairs(pos+1, n, u, l, nums, dp);
                }
                ans = add(ans, dp[pos+1][u]);
            }
        }
        return ans%mod;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int> > dp(n+1, vector<int>(51, -1));

        return countOfPairs(0, n, 0, 51, nums, dp);
    }
};
