using ll = long long;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<ll> prev(nums[0]+1, 1);

        partial_sum(prev.begin(), prev.end(), prev.begin());
        for(int i=1;i<nums.size();i++){
            vector<ll> pcurr(nums[i]+1);
            for(int j=0;j<=nums[i];j++){
                auto ind = max(min(nums[i-1], min(j, j+nums[i-1]-nums[i])), -1);
                if(ind == -1) pcurr[j] = 0;
                else{
                    pcurr[j] = prev[ind];
                }
                pcurr[j] = (pcurr[j] + ((j > 0)?pcurr[j-1]:0))%1000000007;
            }
            prev = pcurr;
        }

        return prev.back();
    }
};
