public class Solution {
    private static final int MOD = 1_000_000_007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int maxVal = 50;  // nums[i] <= 50
        int[][] dp = new int[n][maxVal + 1];

        // Initialize dp for the first element
        for (int a = 0; a <= nums[0]; a++) {
            int b = nums[0] - a;
            if (b >= 0) {
                dp[0][a] = 1;
            }
        }

        // Fill dp table
        for (int i = 1; i < n; i++) {
            int[][] newDp = new int[n][maxVal + 1];
            for (int a = 0; a <= nums[i]; a++) {
                int b = nums[i] - a;
                if (b >= 0) {
                    for (int prevA = 0; prevA <= a; prevA++) {
                        int prevB = nums[i-1] - prevA;
                        if (prevB >= b) {
                            newDp[i][a] = (newDp[i][a] + dp[i - 1][prevA]) % MOD;
                        }
                    }
                }
            }
            dp = newDp;
        }

        // Sum up all valid sequences where arr1[n-1] = a
        int result = 0;
        for (int a = 0; a <= nums[n-1]; a++) {
            result = (result + dp[n-1][a]) % MOD;
        }

        return result;
    }
}
