class Solution {
public:
    const int MOD=1e9+7;
    int dp[2001][51][51]={};
    int find(int i,int prev1,int prev2,int&ans,vector<int>&nums){
        if(i==nums.size()){
            return 1;
            // ans=(ans%MOD+1)%MOD;
            // return;
        }
        if(dp[i][prev1][prev2]!=-1)return dp[i][prev1][prev2];
        int curr_ans=0;
        int x=nums[i];
        for(int j=prev1;j<=x;j++){
            if(x-j<=prev2){
                curr_ans=(curr_ans%MOD+find(i+1,j,x-j,ans,nums)%MOD)%MOD;
            }
        }
        return dp[i][prev1][prev2]=(curr_ans%MOD);
    }
    int countOfPairs(vector<int>& nums) {
        int ans=0;
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        int prev1=0;
        int prev2=*max_element(nums.begin(),nums.end());
        find(0,prev1,prev2,ans,nums);
        return dp[0][prev1][prev2];
    }
};
