class Solution {
public:
    int dp[2001][51][51][2], n;
    const int M = 1e9 + 7;  
    vector<int> a;

     int get(int i, int p1, int p2,int ok2){
        if(i == n){
            return ok2;
        }
         int &ret = dp[i][p1][p2][ok2];
        if(ret != -1) return ret;
        long long int res = 0;
        for(int j = p1; j <= a[i]; j++){
            res = (res + get(i+1,j,a[i] - j,(ok2&(p2 >= (a[i] - j))))) % M;
        }
        return ret = res % M;
    }
    /*
    [40,40,40,40,41,42,43,44,45,45]
 [26,6,20,49,24,13,17,6,12,48,49,34,1,8,40,23,2,39,33,43,1,16,5,34,17]
272278080
Expected:
272278100
    */
    int countOfPairs(vector<int>& nums) {
        a = nums;
        n = a.size();
        memset(dp, - 1, sizeof(dp));

        return get(0,0,a[0],1) % M;
    }
};
