class Solution {
    final int MOD=1000000007;
    public long solve(int[] values,int index,int val1,int val2,long[][][] memo){
        if (index >= values.length) return 1;
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        long result = 0;
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result = (result + solve(values, index + 1, i, j, memo)) % MOD;
                }
            }
        }
        memo[index][val1][val2] = result;
        return result;
    }
    public int countOfPairs(int[] nums) {
        long[][][] memo = new long[nums.length][51][51];
        for (long[][] matrix : memo) {
            for (long[] row : matrix) {
                Arrays.fill(row, -1);
            }
        }
        return (int) solve(nums, 0, 0, nums[0], memo);
    }
}
