class Solution {
    private static final int MOD = 1_000_000_007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n][51];

        // Initialize dp for the first element
        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }

        // Fill dp array
        for (int i = 1; i < n; i++) {
            int[][] newDp = new int[n][51];
            for (int j = 0; j <= nums[i]; j++) {
                int arr2Val = nums[i] - j;
                for (int k = 0; k <= j; k++) {
                    if (arr2Val <= nums[i - 1] - k) {
                        newDp[i][j] = (newDp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
            dp = newDp;
        }

        // Sum all possible final states
        int result = 0;
        for (int j = 0; j <= 50; j++) {
            result = (result + dp[n - 1][j]) % MOD;
        }

        return result;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        // Test case 1
        int[] nums1 = {2, 3, 2};
        System.out.println(solution.countOfPairs(nums1)); // Expected Output: 4

        // Test case 2
        int[] nums2 = {5, 5, 5, 5};
        System.out.println(solution.countOfPairs(nums2)); // Expected Output: 126
    }
}
