class Solution {
    int dp[2002][52][52] ;
    int mod = 1e9+7 ;
    int rec(int i, int last1, int last2, vector<int>& nums){
        if(i==nums.size()){
            return 1 ;
        }
        if(dp[i][last1][last2]!=-1) return dp[i][last1][last2] ;
        int cnt=0 ;
        for(int j=last1;j<=nums[i];j++){
            if(last2>=nums[i]-j){
                cnt += rec(i+1,j,nums[i]-j,nums) ;
                cnt %= mod ;
            }
        }

        return dp[i][last1][last2] = cnt%mod ;
    }
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp)) ;
       int ans=0 ;
       for(int j=0;j<=nums[0];j++){
           ans += rec(1,j,nums[0]-j,nums) ;
           ans %=mod ;
       }

       return ans%mod ;
    }
};
