MOD = 1000000007

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # how prefixes are there to end up with arr1[j-1] = k
        lastvalcounts = []
        for i in range(1001):
            if i<=nums[0]:
                lastvalcounts.append(1)
            else:
                lastvalcounts.append(0)

        for j in range(1,len(nums)):
            # print(lastvalcounts)
            newlastvalcounts = [0]*1001
            for old1 in range(nums[j]+1):
                # first determine the smallest new value of arr1
                old2 = nums[j-1] - old1
                new1 = max(old1, nums[j]-old2)
                if new1 <= nums[j]:
                    newlastvalcounts[new1] += lastvalcounts[old1]
            for i in range(1,nums[j]+1):
                newlastvalcounts[i] = (newlastvalcounts[i] + newlastvalcounts[i-1]) % MOD
            lastvalcounts = newlastvalcounts
        return sum(lastvalcounts) % MOD
