#define MOD 1000000007
class Solution {
public:
  int helper(vector<int>& nums,int i,int prev_val,vector<vector<long long>> &dp,int n){
    if(i==n){
      return 1;
    }
    if(dp[i][prev_val]!=-1){
      return dp[i][prev_val];
    }
    long long ans=0;
    for(int j=0;j<=nums[i];j++){
      int val1=j;
      int val2=nums[i]-j;
      if(i==0){
        ans=((ans%MOD)+(helper(nums,i+1,val1,dp,n)%MOD))%MOD;
      }
      else{
        if(val1>=prev_val && val2<=nums[i-1]-prev_val){
          ans=((ans%MOD)+(helper(nums,i+1,val1,dp,n)%MOD))%MOD;
        }
      }
    }

    return dp[i][prev_val]=(ans%MOD);

  }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<long long>> dp(n,vector<long long>(51,-1));
        return (helper(nums,0,0,dp,n)%MOD);
    }
};
