from typing import List

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7

        dp = [[[0 for _ in range(51)] for _ in range(51)] for _ in range(n + 1)]

        for j in range(nums[0] + 1):
            dp[1][j][nums[0] - j] = 1

        for i in range(2, n + 1):
            for j in range(nums[i - 1] + 1):
                k = nums[i - 1] - j
                for prev_j in range(j + 1):
                    prev_k = nums[i - 2] - prev_j
                    if prev_k >= k:
                        dp[i][j][k] = (dp[i][j][k] + dp[i - 1][prev_j][prev_k]) % MOD

        return sum(dp[n][j][k] for j in range(51) for k in range(51)) % MOD
