class Solution {
public:
    int rec(int i, const std::vector<int>& nums, std::vector<int>& arr1, std::vector<int>& arr2, int mod, std::vector<std::vector<int>>& memo) {
        if (i == nums.size()) {
            return 1;
        }

        if (!arr1.empty() && !arr2.empty() && memo[i][arr1.back()] != -1) {
            return memo[i][arr1.back()];
        }

        int count = 0;
        for (int a = 0; a <= nums[i]; ++a) {
            int b = nums[i] - a;
            if ((arr1.empty() || arr1.back() <= a) && (arr2.empty() || arr2.back() >= b)) {
                arr1.push_back(a);
                arr2.push_back(b);
                count = (count + rec(i + 1, nums, arr1, arr2, mod, memo)) % mod;
                arr1.pop_back();
                arr2.pop_back();
            }
        }

        if (!arr1.empty()) {
            memo[i][arr1.back()] = count;
        }

        return count;
    }

    const int MOD = 1e9+7;

    int countOfPairs(vector<int>& nums) {
        const int mod = 1e9 + 7;
        std::vector<int> arr1, arr2;
        int maxNum = *max_element(nums.begin(), nums.end());
        std::vector<std::vector<int>> memo(nums.size(), std::vector<int>(maxNum + 1, -1));
        return rec(0, nums, arr1, arr2, mod, memo);
    }
};
