class Solution {
public:
    const int Z = 1e9 + 7;
    vector<int> nums;
    vector<vector<int>> dp;
    int n;
    int rec(int level, int x2)
    {
        //prunning

        if(level==n)return 1;

        if(dp[level][x2]!=-1)return dp[level][x2];

        int x1 = nums[level-1]-x2;
        int ans=0;
        for(int nx2=x2;nx2>=0;nx2--)
        {
            int nx1 = nums[level]-nx2;
            if(nx1<x1)continue;
            ans = (ans + rec(level+1,nx2))%Z;
        }
        return dp[level][x2]=ans;
    }
    int countOfPairs(vector<int>& arr) {
        nums=arr;
        n=arr.size();
        dp.resize(n,vector<int>(51,-1));
        int ans=0;
        for(int i=0;i<=nums[0];i++)
        {
            ans = (ans + rec(1,i))%Z;
        }
        return ans;
    }
};
