class Solution {

    private static final int MOD = 1_000_000_007;
    private static Map<String, Integer> memo;

    public int countOfPairs(int[] arr) {
        memo = new HashMap<>();
        return generatePairs(arr, 0, 0, Integer.MAX_VALUE);
    }

    private static int generatePairs(int[] arr, int index, int prev1, int prev2) {
        if (index == arr.length) {
            return 1;
        }

        String key = index + "," + prev1 + "," + prev2;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        int count = 0;
        for (int i = prev1; i <= arr[index]; i++) {
            int j = arr[index] - i;
            if (j <= prev2) {
                count = (count + generatePairs(arr, index + 1, i, j)) % MOD;
            }
        }

        memo.put(key, count);
        return count;
    }
}
