class Solution {
public:
const int mod=1e9+7;
    long long helper(int ind,int prev,vector<int>&nums,vector<vector<long long>>&dp){
        if(ind>=nums.size()) return 1;
        if(dp[ind][prev+1]!=-1) return dp[ind][prev+1];
        long long res=0;
        for(int i=0;i<=nums[ind];i++){
            if(ind==0 || (i>=prev && (nums[ind]-i)<=(nums[ind-1]-prev))){
                res+=helper(ind+1,i,nums,dp);
                res%=mod;
            }
        }
        return dp[ind][prev+1]=res%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<long long>>dp(2001,vector<long long>(55,-1));
        return helper(0,-1,nums,dp);
        // return 0;
    }
};
