#define ll long long
#define MOD 1000000007
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int r = *max_element(nums.begin(),nums.end());
        vector<vector<ll>> dp(n,vector<ll>(r+1,0));
        for(int rv = 0; rv <= nums[0];++rv){
            dp[0][rv] = 1;
        }
        for(int i = 1; i < n; ++i){
            // #valid ways that i-th elmement ends in the number j in the first array.
            //dp1[i][j]

            // vector<ll> pfx(r+1);
            // for(int rv = 0; rv <= r; ++rv){
            //     pfx[rv] = (rv?pfx[rv-1]:0) + dp[i-1][rv];
            //     pfx[rv] %= MOD;
            // }
            // for(int rv = 0; rv <= nums[i]; ++ rv){
            //     int ext = nums[i] - rv;
            //     if(ext >= 0){
            //         cout << pfx[rv] << endl;
            //         dp[i][rv] = pfx[rv]%MOD;
            //     }
            // }
            // cout << endl;


            for(int rv = 0; rv <= nums[i];++rv){
                int other = nums[i]-rv;
                for(int ov = 0; ov <= rv; ++ov){
                    if(nums[i-1]-ov >= other){
                        dp[i][rv] += dp[i-1][ov];
                        dp[i][rv]%=MOD;
                    }

                }
            }
        }

        int out = 0;
        for(int rv = 0; rv <= nums[n-1]; ++ rv){
            out += dp[n-1][rv];
            out %= MOD;
        }
        return out;

    }
};
