class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1e9+7;
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));
        for(int i=0; i<51; i++){
            for(int j=0; j<51; j++){
                if(i+j == nums[0]){
                    dp[0][i][j] = 1;
                }
            }
        }
        for(int i=1; i<n; i++){
            for(int j=0; j<51; j++){
                for(int k=0; k<51; k++){
                    if(j+k == nums[i]){
                        for(int x=0; x<=j; x++){
                            int x_ = nums[i-1]-x;
                            if(x_ >= 0 && x_ <= 50 && x_ >= k){
                                dp[i][j][k] = (dp[i][j][k]+dp[i-1][x][x_])%mod;
                            }
                        }
                        for(int y=k; y>51; j++){
                            int y_ = nums[i-1]-y;
                            if(y_ >= 0 && y_ <= 50 && y_ <= j){
                                dp[i][j][k] =(dp[i][j][k]+dp[i-1][y][y_])%mod;
                            }
                        }
                    }
                }
            }
        }
        int count = 0;
        for(int i=0; i<51; i++){
            for(int j=0; j<51; j++){
                if(i+j == nums[n-1])count = (count+dp[n-1][i][j])%mod;
            }
        }
        return count;
    }
};
