class Solution {
public:

    const int mod = 1e9 + 7;

    int solve(vector<int>& nums, int ind, int prev, int temp, vector<vector<vector<int>>>&dp) {
        if (ind >= nums.size()) return 1; 

        if (dp[ind][prev][temp] != -1) return dp[ind][prev][temp]; 

        int res = 0;
        for (int i = 0; i <= nums[ind]; ++i) {
            int val1 = i;
            int val2 = nums[ind] - i;
            if (val1 >= prev && val2 <= temp) {
                res = (res + solve(nums, ind + 1, val1, val2,dp)) % mod;
            }
        }

        dp[ind][prev][temp] = res;
        return res;

    }
    int countOfPairs(vector<int>& nums) {
        int n= nums.size();
        //max poss -> 50 
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, 50,dp);
    }
};
