#include <iostream>
#define ll long long int
class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
        int si=nums.size();

        vector<vector<vector<ll>>> store(si,vector<vector<ll>>(51,vector<ll>(51,-1))); // done for a range of nums[i]

        function<ll(int,int,int)>answer=[&](int ind,int d1,int d2)->ll{
            if(ind>=nums.size()) return 1;
            ll res=0;

            ll mod=1e9+7;
            if(store[ind][d1][d2]!=-1) 
            {
                return store[ind][d1][d2];
            }
            for(int i=d1;i<=nums[ind];i++)
            {
                for(int j=d2;j>=0;j--)
                {
                    if(i+j==nums[ind])
                    {
                        res+=answer(ind+1,i,j);
                        res=res%mod;
                    }
                }
            }
            return store[ind][d1][d2]=res;
        };
        return answer(0,0,nums[0]);
    }
};
