class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        memo = {}
        n = len(nums)
        def dp(i, prev1, prev2):
            if i == n:
                return 1
            if (i, prev1, prev2) in memo:
                return memo[(i, prev1, prev2)]
            num = nums[i]
            cnt = 0
            for a in range(num+1):
                b = num - a
                if a >= prev1 and b <= prev2:
                    cnt += dp(i+1, a, b)
            memo[(i,prev1, prev2)] = cnt % MOD
            return memo[(i,prev1, prev2)]
        ans = dp(0, -math.inf, math.inf)
        # print(memo)
        return ans
