class Solution {
    using ll = long long;
    int n;
    ll m = 1e9+7;

    ll solve(int id , int x , int y, vector<int>& nums , vector<vector<vector<ll>>>& dp){
        if(id == n) return 1;
        if(dp[id][x][y] != -1) return dp[id][x][y];

        ll ans = 0;
        for(int i = x ; i <= nums[id] ; i++){
            for(int j = y ; j >= 0 ; j-- ){
                if(i + j != nums[id]) continue;
                ans += solve(id+1, i , j , nums , dp);
                ans %= m;
            }
        }
        return dp[id][x][y] = ans;

    }
public:
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        vector<vector<vector<ll>>> dp(n , vector<vector<ll>>(55 , vector<ll>(55, -1)));
        ll ans = solve(0 , 0 , nums[0],nums , dp);
        ans %= m;
        return int(ans);
    }
};
