class Solution {
public:
    int MOD = 1e9 + 7;
    int fun(int i, int prev, vector<int>& nums, vector<vector<int>>& dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev]!=-1){
            return dp[i][prev];
        }
        int count = 0;
        for(int j1 = prev;j1<=nums[i];j1++){
            int j2 = nums[i]-j1;
            if(i==0 || j2<=nums[i-1] - prev){
                count = (count + fun(i+1,j1,nums,dp)) % MOD;
            }
        }
        return dp[i][prev] = count;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(51,-1));
        return fun(0,0,nums,dp);
    }
};
