class Solution {
public:
    long dp[2001][55][55];
    int mod = 1000000007;
    int solve(vector<int> &nums, int si, int prev1, int prev2){
        if(si == nums.size()) return 1;
        if(dp[si][prev1][prev2] != -1) return dp[si][prev1][prev2];

        long ans = 0;
        for(int i=prev1;i<=nums[si];i++){
            int s = i;
            int e = nums[si]-s;
            if(e > prev2) continue;
            ans += solve(nums, si+1, s, e);
            ans %= mod;
        }

        dp[si][prev1][prev2] = ans;
        return dp[si][prev1][prev2];
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        for(int i = 0;i<n;i++){
            for(int j = 0;j<=50;j++){
                for(int k = 0;k<=50;k++){
                    dp[i][j][k] = -1;
                }
            }
        }
        return solve(nums, 0, 0, nums[0]);
    }
};
