#define rep(i, a) for (int i = 0; i < (int)(a); i++)
#define all(a) a.begin(), a.end()
#define rall(a) a.rbegin(), a.rend()
using ll = long long;
const int mod = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        auto a = nums;
        vector<int> dp(a[0] + 1, 1);
        rep(i, n - 1) {
            vector<int> p(a[i + 1] + 1);
            swap(p, dp);
            for (int j = 0; j <= a[i]; j++) {
                for (int k = 0; k <= a[i + 1]; k++) {
                    if (j > k) continue;
                    int x = a[i] - j, y = a[i + 1] - k;
                    if (x >= y) {
                        dp[k] += p[j];
                        dp[k] %= mod;
                    }
                }
            }
        }
        int ans = 0;
        for (int i = 0; i <= a[n - 1]; i++) {
            ans += dp[i];
            ans %= mod;
        }
        return ans;
    }
};
