#define ll long long
ll mod = 1e9+7;

class Solution {
    ll dp[2002][52];
    int n;

    ll f(int ind, int prev, vector<int>&nums)
    {
        if(ind >= n) return 1;

        if(dp[ind][prev+1] != -1) return dp[ind][prev+1];

        int s1 = -1;
        if(prev != -1) s1 = prev;
        int s2 = 1000;
        if(prev != -1) s2 = nums[ind-1] - prev;
        ll res = 0;
        for(int i=0; i<=nums[ind];i++)
        {
            if(i>=s1 && (nums[ind]-i <= s2))
            {
                res = (res + f(ind+1,i,nums))%mod;
            }
        }
        return dp[ind][prev+1] = res;
    }

public:
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(dp,-1,sizeof(dp));
        return f(0,-1,nums);
    }
};
