class Solution {
    private val MOD = 1_000_000_007

    fun solve(ind: Int, ind1: Int, ind2: Int, nums: IntArray, dp: Array<Array<IntArray>>): Long {
        if (ind >= nums.size) return 1

        if (dp[ind][ind1][ind2] != -1) return dp[ind][ind1][ind2].toLong()

        var ans: Long = 0
        for (i in ind1..nums[ind]) {
            for (j in 0..ind2) {
                if (i + j == nums[ind]) {
                    ans = (ans + solve(ind + 1, i, j, nums, dp)) % MOD
                }
            }
        }
        dp[ind][ind1][ind2] = ans.toInt()
        return ans
    }

    fun countOfPairs(nums: IntArray): Int {
        val n = nums.size
        val dp = Array(n) { Array(51) { IntArray(51) { -1 } } }
        return solve(0, 0, nums[0], nums, dp).toInt()
    }
}
