func countOfPairs(nums []int) int {

    MOD := 1000000007
    n := len(nums)
    var dp func(startPos, prevArr1, prevArr2 int) int

    memo := make(map[int]map[int]map[int]int)


    dp = func(start, prevArr1, prevArr2 int) int {
        if start == n {
            return 1
        }

        if val, ok := memo[start][prevArr1][prevArr2]; ok {
            return val
        }

         if _, ok := memo[start]; !ok {
            memo[start] = make(map[int]map[int]int)
        }

        if _, ok := memo[start][prevArr1]; !ok {
            memo[start][prevArr1] = make(map[int]int)
        }

        ans := 0
        for i := prevArr1; i <= nums[start]; i++ {
            arr2Val := nums[start] - i
            if arr2Val <= prevArr2 && arr2Val >= 0 { // array is valid
                ans += dp(start + 1, i, arr2Val)
                ans = ans % MOD
            }
        }

        memo[start][prevArr1][prevArr2] = ans % MOD
        return memo[start][prevArr1][prevArr2]
    }

    return dp(0, 0, math.MaxInt) % MOD

}
