class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), md = 1e9 + 7;
        vector<vector<int>> dp(51, vector<int>(n + 1, 0));
        for(int i = 0; i <= nums[0]; i++)dp[i][0] = 1;
        for(int i = 1; i < n; i++){
            for(int x = 0; x <= nums[i]; x++){
                int y = nums[i] - x;
                int cnt = 0;
                for(int z = 0; z <= nums[i-1]; z++){
                    int t = nums[i-1] - z;
                    if(x >= z && t >= y){
                        dp[x][i] = (dp[x][i] + dp[z][i-1]) % md;
                    }
                }
                //cout << dp[x][i] << " ";
            }
            //cout << "\n";
        }
        int xx = 0;
        for(int i = 0; i <= 50; i++)xx = (dp[i][n-1]+ xx) % md;
        return xx;
    }
};
