#define ll long long

class Solution {
public:
    vector<vector<vector<ll>>> dp;
    int mod=1e9+7;
    int solve(int i,int prev1,int prev2,vector<int>& nums){
        int n=nums.size();
        if(i==n){
            return 1;
        }

        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        ll cnt=0;
        for(int k=0;k<=nums[i];k++){
            int val1=k;
            int val2=nums[i]-k;
            if((val1>=prev1 && val2<=prev2) ||( prev1==51 && prev2==51)){
                cnt=(cnt+solve(i+1,val1,val2,nums))%mod;
            }

        }
        return dp[i][prev1][prev2]=cnt;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.resize(n,vector<vector<ll>>(52,vector<ll>(52,-1)));
        return solve(0,51,51,nums)%mod;
    }
};
