class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<long long>>dp(n,vector<long long>(51,0));
        int mod=1000000007;
        for(int y=0;y<=nums[0];y++)dp[0][y]=1;
        for(int x=1;x<n;x++){
            for(int y=0;y<=nums[x];y++){
                for(int z=0;z<=y;z++){
                    if(nums[x-1]-z>=nums[x]-y)dp[x][y]+=dp[x-1][z];
                    dp[x][y]=(dp[x][y]%mod);
                }
            }
        }
        int ans=0;
        for(int x=0;x<=nums[n-1];x++){
            ans+=dp[n-1][x];
            ans=(ans%mod);
        }
        return ans;
    }
};
