class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<int>> dp;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxVal = *max_element(nums.begin(), nums.end());
        dp = vector<vector<int>>(n, vector<int>(maxVal + 1, -1));
        return count(0, 0, nums);
    }

private:
    int count(int i, int last, vector<int>& nums) {
        if (i == nums.size()) {
            return 1;  
        }

        if (dp[i][last] != -1) {
            return dp[i][last];
        }

        int result = 0;
        int upper = nums[i];

        for (int x = last; x <= upper; ++x) {
            int y = nums[i] - x;
            if (i == 0 || y <= (nums[i - 1] - last)) {
                result = (result + count(i + 1, x, nums)) % MOD;
            }
        }

        return dp[i][last] = result;
    }
};
