class Solution {
public:
    int mod=1e9+7;
    int dp[2001][51];
    int func(int ind,int n,vector<int>&nums,int prev1){
        if(ind>=n) return 1;
        if(dp[ind][prev1]!=-1) return dp[ind][prev1];
        int ans=0;
        int prev2;
        if(ind==0) prev2=50;
        else prev2=nums[ind-1]-prev1;
        for(int i=prev1; i<=nums[ind]; i++){
            int x=nums[ind]-i;
            if(x<=prev2 && x>=0){
                ans=(ans+func(ind+1,n,nums,i))%mod;
            }
        }
        return dp[ind][prev1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        // int prev1=0,prev2=nums[0];
        memset(dp,-1,sizeof(dp));
        return func(0,n,nums,0);
    }
};
